mapper.select:
SELECT 'prd_' || pl.prd_lot_id AS id, pl.prd_lot_code || ' ' || cd.det_nm || ' (' || cd2.det_nm || ')' AS text, 1 AS children FROM product_lot pl JOIN suju_order_detail sd ON pl.odd_id = sd.odd_id JOIN product_option po ON sd.opt_id = po.opt_id JOIN product p ON po.prd_id = p.prd_id JOIN code_detail cd ON p.prd_type = cd.det_id JOIN code_detail cd2 ON pl.prd_lot_status = cd2.det_id
AND pl.prd_lot_code LIKE '%' || #{lotCode} || '%'
AND pl.prd_lot_status = #{lotStatus}
AND cd.det_id = #{prdType}

SELECT	prd_lot_id as "prd_lot_id"
from	product_lot
where	odd_id = #{odd_id}
and		prd_lot_sort = #{prd_lot_sort}

mapper.insert:
INSERT INTO prdLot_processLot_mapping (
    prd_lot_id,
    prc_lot_id
) VALUES (
    #{prdLotId},
    #{prcLotId}
)

mapper.select:
SELECT mat_cd
FROM material
WHERE mat_id = #{mat_id}

mapper.update:
UPDATE product_lot
SET prd_lot_status = 'lot_status_03' -- ✅ 완료 상태로 변경
WHERE odd_id = #{oddId}
