mapper.select:
SELECT COUNT(*) 
  FROM bom
 WHERE opt_id = #{opt_id}

mapper.insert:
INSERT ALL

    INTO BOM (
    	      OPT_ID
    	    , MAT_ID
    	    , BOM_CNT
    	    , BOM_UNIT
    	    , BOM_COMM
    		, BOM_REG_DATE
    	   )
    VALUES (
              #{bom.OPT_ID}
            , #{bom.MAT_ID}
            , #{bom.BOM_CNT}
            , #{bom.BOM_UNIT}
            , #{bom.BOM_COMM}
            , SYSDATE)

SELECT 1 FROM DUAL

mapper.update:
MERGE INTO BOM target USING (
SELECT #{bom.BOM_ID} AS BOM_ID, #{bom.OPT_ID} AS OPT_ID, #{bom.MAT_ID} AS MAT_ID, #{bom.BOM_CNT} AS BOM_CNT, #{bom.BOM_UNIT} AS BOM_UNIT, #{bom.BOM_COMM} AS BOM_COMM FROM DUAL
) src ON (target.BOM_ID = src.BOM_ID) WHEN MATCHED THEN UPDATE SET target.BOM_CNT = src.BOM_CNT, target.BOM_UNIT = src.BOM_UNIT, target.BOM_COMM = src.BOM_COMM, target.BOM_MOD_DATE = SYSDATE

mapper.delete:
DELETE FROM BOM
WHERE BOM_ID IN

    #{bomId}
